{
  "product_name": "Precision Manufacturing Quality Control (PMQC) System",
  "overview": {
    "description": "The PMQC System is an advanced automated inspection platform that delivers micron‑level precision for dimensional, angular and surface quality validation. It combines photo‑based measurement, intelligent segmentation, real‑time pass/fail logic, and statistical process control (SPC) integration to give manufacturers instant quality decisions and full audit trail compliance.",
    "key_capabilities": [
      "Multi‑point measurement analysis",
      "Angular and dimensional measurements",
      "CAD comparison verification",
      "Statistical Process Control integration",
      "Intelligent image segmentation (85 % manual time reduction)",
      "Real‑time validation with instant alerts",
      "Integrated quality‑control dashboard",
      "Seamless ERP & QMS data export",
      "Customizable compliance reporting"
    ],
    "technical_specifications": {
      "precision": "Micron‑level",
      "multi_dimensional": true,
      "real_time_processing": true,
      "interface": "Web + API"
    }
  },

  "get_started": {
    "prerequisites": [
      {
        "id": "hardware",
        "description": "Industrial camera (≥12 MP), high‑speed optics, vibration‑isolated mounting plate."
      },
      {
        "id": "software",
        "description": "Operating System: Windows 10/11 or Red Hat Enterprise Linux 8+, .NET 6 SDK or .NET Core 6 runtime."
      },
      {
        "id": "network",
        "description": "LAN or Wi‑Fi connectivity to ERP/QMS, firewall rules: TCP 8000‑8001 allowed."
      },
      {
        "id": "calibration",
        "description": "Calibration fixture (micrometer scale) and test part for first‑time set‑up."
      }
    ],
    "installation": [
      "Mount the camera on the fixture and connect to power.",
      "Plug the Ethernet cable into the PC and into the network switch.",
      "Run the Install_Batch.exe (or use the silent installer: `Install_Batch.exe /S`).",
      "Follow on‑screen prompts: accept EULA, specify camera IP, set calibration fixture coordinates.",
      "After installation, launch the PMQC desktop client."
    ],
    "first_run": [
      "Open Settings > Calibration. Load the calibration fixture and capture 5 reference images.",
      "Run the auto‑tilt correction sub‑routine to compensate for any mounting tilt.",
      "Select a sample component and run a full scan to verify measurement parameters.",
      "Confirm that the real‑time validation logic flags the component correctly.",
      "Generate a test report and verify all data fields populate correctly.",
      "Schedule a daily export to the test ERP database for compliance verification."
    ]
  },

  "how_to_use": {
    "measurement_setup": [
      {
        "step": 1,
        "description": "Define Measurement Profiles (Profile Manager).",
        "details": [
          "Specify critical dimensions, tolerances, and acceptable ranges.",
          "Add angular measurement jobs (e.g., Angle J = ± 0.5°).",
          "Enable CAD comparison: load reference CAD file, align via surface matching."
        ]
      },
      {
        "step": 2,
        "description": "Configure Image Segmentation.",
        "details": [
          "Enable Intelligent Segmentation to let the ML model auto‑detect measurement points.",
          "If automatic detection is unreliable, manually annotate at least 3 guide points to retrain the segmenter."
        ]
      },
      {
        "step": 3,
        "description": "Set Validation Rules.",
        "details": [
          "Pass/Fail threshold set to ± 0.02 mm for dimensional checks.",
          "Define alert escalation: email to supervisor, stop line if cumulative out‑of‑spec > 5 %."
        ]
      },
      {
        "step": 4,
        "description": "Queue the part for inspection.",
        "details": [
          "Scan with the camera; system captures image, applies segmentation, computes measurements.",
          "Displays results: Measurement N, Status, Comparison to CAD, timestamp."
        ]
      }
    ],
    "validation_process": [
      "Real‑time comparison to tolerances occurs immediately after measurement.",
      "If pass, the component proceeds to the next station; if fail, the system writes the component to a non‑conforming bin.",
      "Operator may override a fail status after manual review; system logs a reason code."
    ],
    "reporting": [
      "All scans are stored in a structured log with time‑stamp, operator ID, and machine ID.",
      "Generate batch report: File > Export > Batch Report (CSV/JSON/XML).",
      "Dashboard analytics: View quality trends, SPC charts, and out‑of‑spec heat maps."
    ],
    "dashboard_usage": [
      "Immediate alerts: pop‑up and email for out‑of‑spec components.",
      "Trend monitoring: click ‘Batch Trends’ to see historical QC metrics over the last 30 days.",
      "Compliance view: export a PDF audit trail with signed attestations."
    ]
  },

  "edge_cases_and_troubleshooting": [
    {
      "id": "segmentation_error",
      "description": "Model fails to detect key measurement points.",
      "symptoms": [
        "Blank measurement fields",
        "High variance in repeated scans"
      ],
      "resolution": [
        "Refresh the segmentation model via Settings > ML > Retrain.",
        "Add manual reference points in the segmentation editor.",
        "Verify camera alignment and illumination."
      ]
    },
    {
      "id": "camera_misalignment",
      "description": "Measured values drift due to sensor tilt.",
      "symptoms": [
        "Consistent offset from expected CAD values"
      ],
      "resolution": [
        "Run the Tilt‑Correction wizard.",
        "Re‑mount the camera and perform a new calibration."
      ]
    },
    {
      "id": "calibration_drift",
      "description": "Long‑term drift after several hours of operation.",
      "symptoms": [
        "Increasing out‑of‑spec rate after 6 h",
        "Time‑stamp mismatch in logs"
      ],
      "resolution": [
        "Schedule automatic calibration checks every 4 h.",
        "Use the built‑in auto‑calibration feature."
      ]
    },
    {
      "id": "network_failure",
      "description": "Loss of connectivity to ERP during export.",
      "symptoms": [
        "Export errors, “Network Unreachable” dialog"
      ],
      "resolution": [
        "Verify switch health, ensure port is active.",
        "Confirm firewall not blocking port 8001.",
        "Use the background export queue; it will retry automatically."
      ]
    },
    {
      "id": "operator_override",
      "description": "Manual override of a fail status.",
      "symptoms": [
        "Component proceeds after “Fail” status"
      ],
      "resolution": [
        "Always enter reason code; system logs justification for audit.",
        "If overrides exceed 10 % of total parts, trigger manager review."
      ]
    }
  ],

  "integration": {
    "erp_export": {
      "description": "Real‑time push of inspection data to ERP.",
      "endpoints": [
        {
          "url": "https://erp.company.com/api/inspection",
          "method": "POST",
          "auth": "OAuth2.0 bearer",
          "payload": {
            "batch_id": "string",
            "timestamp": "ISO8601",
            "results": [
              {
                "part_id": "string",
                "measurement_n": "float",
                "status": "PASS/FAIL",
                "raw_image_url": "string"
              }
            ]
          }
        }
      ],
      "retry_policy": {
        "max_attempts": 5,
        "backoff_seconds": 30
      }
    },
    "qms_integration": {
      "description": "Export of audit trail and SPC data to the Quality Management System.",
      "methods": [
        "Export CSV via File > Export > QMS Export",
        "Webhooks: POST to QMS webhook URL configured under Settings > Integration > QMS"
      ]
    },
    "api_reference": {
      "base_url": "https://api.pmqc.company.com/v1",
      "endpoints": [
        "/profiles",
        "/measurements",
        "/reports",
        "/alerts"
      ]
    }
  },

  "faq": [
    {
      "question": "Can the system run in a high‑temperature environment?",
      "answer": "Yes. The camera housing is rated to 60 °C and the software supports external temperature sensor input for adaptive lens compensation."
    },
    {
      "question": "What compliance standards does PMQC meet?",
      "answer": "ISO 9001:2015, ISO 14224 for equipment, and FDA 21CFR Part 820 audit trail requirements."
    }
  ]
}
